import React from "react";
import { View, Image, FlatList, SafeAreaView } from "react-native";
import { Feather } from "@expo/vector-icons";
import { StatusBar } from "expo-status-bar";
import { AppStyles as styles } from "./FormatStyle/Styles";
import Post from "./Post";

const images = [
    {
        id: 1,
        name: 'Huấn Hoa Hồng',
        image: require('./assets/post1.jpg'),
        likeCount: 128,
        avatar: require('./assets/avatar1.jpg')
    },
    {
        id: 2,
        name: 'Bà Tân Vlog',
        image: require('./assets/post2.jpg'),
        likeCount: 100,
        avatar: require('./assets/avatar2.jpg')
    },
    {
        id: 3,
        name: 'Khá Bảnh',
        image: require('./assets/post3.jpg'),
        likeCount: 500,
        avatar: require('./assets/avatar3.jpg')
    },
    {
        id: 4,
        name: 'Sơn Tùng M-tp',
        image: require('./assets/post4.png'),
        likeCount: 1000,
        avatar: require('./assets/avatar4.jpg')
    },
];

export default function App() {
    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.headerContainer}>
                <Image
                    source={require("./assets/instagram.png")}
                    style={styles.instagramImage}
                    resizeMode="contain"
                />
                <Feather name="inbox" size={27} color="black" />
            </View>
            <FlatList
                data={images}
                keyExtractor={(item, index) => item.id + index + "index"}
                renderItem={({ item }) => <Post item={item} />}
            />
        </SafeAreaView>
    );
}


