import { StatusBar } from 'expo-status-bar';
import { Feather } from '@expo/vector-icons';
import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  Image,
  SafeAreaView,
  FlatList,
  Alert
} from 'react-native';

export default function App() {
  return (
    <View name='total'>
      <View style={{width: 30, marginRight: 15}} />
      <SafeAreaView name='head' style={styles.header}>
        <Image
          source={require('./assets/instagram.png')}
          style={styles.instagram}
          resizeMode="contain"
        />

        <Feather
          name="inbox"
          size={30}
          color= 'black'
          style={styles.inboxIcon}
        />
      </SafeAreaView>


      <View name='body'>
        <View name='post'>
          <View name='profile'>
            <Image name='avatar'>
              source={feed.avatar}
              style={styles.avatarImage}
              resizeMode="cover"
            </Image>
            <Text name='posterName'>
              source={feed.posterName}
              style={styles.posterName}
            </Text>
          </View>

        </View>
      </View>
    </View>
  );
}

const feedData = [
  {
    id: 1,
    name: 'Huấn Hoa Hồng',
    image: require('./assets/post1.png'),
    likeCount: 128,
    avatar: require('./assets/avatar1.png')
  },
  {
    id: 2,
    name: 'Bà Tân Vlog',
    image: require('./assets/post2.png'),
    likeCount: 100,
    avatar: require('./assets/avatar2.png')
  },
  {
    id: 3,
    name: 'Khá Bảnh',
    image: require('./assets/post3.png'),
    likeCount: 500,
    avatar: require('./assets/avatar3.png')
  },
  {
    id: 4,
    name: 'Sơn Tùng M-tp',
    image: require('./assets/post4.png'),
    likeCount: 1000,
    avatar: require('./assets/avatar4.png')
  },
];
{feedData.map(feed => {
  return (
    <View style={styles.feedItem} key={feed.id}>
      <View style={styles.avatarWrapper}>
        <Image>
          source={feed.avater}
          style={styles.avatarImage}
          resizeMode="cover"
        </Image>
        <Text 
          style={styles.posterName}
          source={feed.name}
        ></Text>
        <View style={styles.imageWrapper}> 
          <Image
            source={feed.image}
            style={styles.imagePoster}
          ></Image>
        </View>
      </View>
    </View>
  )
})}

const styles = StyleSheet.create({
  header: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: '#f3f6fa',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  instagram: {
    flex: 1,
    width: null,
    height: 45
  },
  inboxIcon: {
    marginRight: 15 
  },
  avatarWrapper: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 7,
    marginHorizontal: 12,
  },
  avatarImage: {
    width: 60,
    height: 60,
    borderRadius: 60/2,
  },
  posterName: {
    marginLeft:12,
    marginRight:15,
    fontWeight: '500',
  },
  imageWrapper:{
    height:300,
  },
  imagePoster: {
    flex: 1,
    width: null,
    height: null,
  }
});
